import axios from "axios";
export const GetPokemonList = (page) => async (dispath) => {
  try {
    dispath({ type: "POKEMON_LIST_LOADING" });
    const perPage = 15;
    const offset = page * perPage - perPage;

    /*  1 = 0
            2 = 15
            3 = 30 3*15 - 15 = 30 */

    const res = await axios.get(
      `https://pokeapi.co/api/v2/pokemon?limit=${perPage}&offset=${offset}`
    );
    dispath({ type: "POKEMON_LIST_SUCCESS", payload: res.data });
  } catch (e) {
    dispath({ type: "POKEMON_LIST_FAIL" });
  }
};

export const GetPokemon = (pokemon) => async (dispatch) => {
  try {
    dispath({ type: "POKEMON_MULTIPLE_LOADING" });
    const perPage = 15;
    const offset = page * perPage - perPage;

    /*  1 = 0
            2 = 15
            3 = 30 3*15 - 15 = 30 */

    const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
    dispath({ type: "POKEMON_MULTIPLE_SUCCESS", payload: res.data });
  } catch (e) {
    dispath({ type: "POKEMON_MULTIPLE_FAIL" });
  }
};
